class Solution(object):
    def maximumSumOfHeights(self, maxHeights):
        """
        :type maxHeights: List[int]
        :rtype: int
        """
        n = len(maxHeights)
        left_max = [0] * n
        right_max = [0] * n
        result = 0

        # Scan from left to right
        for i in range(n):
            if i == 0:
                left_max[i] = maxHeights[i]
            else:
                left_max[i] = min(left_max[i - 1] + 1, maxHeights[i])

        # Scan from right to left
        for i in range(n - 1, -1, -1):
            if i == n - 1:
                right_max[i] = maxHeights[i]
            else:
                right_max[i] = min(right_max[i + 1] + 1, maxHeights[i])

        # Calculate the maximum possible sum of heights
        for i in range(n):
            result += min(left_max[i], right_max[i])

        return result
