from typing import List

class Solution:
    def maximumSumOfHeights(self, maxHeights: List[int]) -> int:
        n = len(maxHeights)
        left = [0] * n
        right = [0] * n
        stack = []

        # Left
        for i in range(n):
            while stack and maxHeights[stack[-1]] < maxHeights[i]:
                stack.pop()
            left[i] = stack[-1] if stack else -1
            stack.append(i)

        stack = []
        # Right
        for i in range(n - 1, -1, -1):
            while stack and maxHeights[stack[-1]] <= maxHeights[i]:
                stack.pop()
            right[i] = stack[-1] if stack else n
            stack.append(i)

        max_sum = 0
        for i in range(n):
            if left[i] != -1 and right[i] != n:
                max_sum += min(maxHeights[left[i]], maxHeights[right[i]]) * (right[i] - left[i] - 1)
            elif left[i] != -1:
                max_sum += maxHeights[left[i]] * (i - left[i])
            elif right[i] != n:
                max_sum += maxHeights[right[i]] * (right[i] - i)
            else:
                max_sum += maxHeights[i]

        return max_sum
