from typing import List

class Solution:
    def maximumSumOfHeights(self, maxHeights: List[int]) -> int:
        def getMaxSum(arr):
            return sum(min(arr[i], arr[i + 1]) for i in range(len(arr) - 1))

        left = [0] * len(maxHeights)
        right = [0] * len(maxHeights)

        # Left scan
        for i in range(len(maxHeights)):
            left[i] = maxHeights[i] if i == 0 else min(left[i - 1] + 1, maxHeights[i])

        # Right scan
        for i in range(len(maxHeights) - 1, -1, -1):
            right[i] = maxHeights[i] if i == len(maxHeights) - 1 else min(right[i + 1] + 1, maxHeights[i])

        # Calculate the maximum sum of the minimum of left and right scans
        max_sum = sum(min(left[i], right[i]) for i in range(len(maxHeights)))
        return max_sum
