impl Solution {
    pub fn maximum_sum_of_heights(max_heights: Vec<i32>) -> i64 {
        let n = max_heights.len();
        let mut result = 0;

        for peak in 0..n {
            let mut sum = max_heights[peak] as i64; // Initialize sum with the peak value
            let mut min_height = max_heights[peak];

            // Expand to the left
            for i in (0..peak).rev() {
                min_height = min_height.min(max_heights[i]);
                sum += min_height as i64;
            }

            min_height = max_heights[peak];

            // Expand to the right
            for i in peak + 1..n {
                min_height = min_height.min(max_heights[i]);
                sum += min_height as i64;
            }

            result = result.max(sum); // Update result if a higher sum is found
        }

        result
    }
}
