object Solution {
    def maximumSumOfHeights(maxHeights: List[Int]): Long = {
        val n = maxHeights.length
        var maxSum: Long = 0

        for (peak <- maxHeights.indices) {
            val leftHeights = new Array[Long](n)
            val rightHeights = new Array[Long](n)

            leftHeights(peak) = maxHeights(peak)
            rightHeights(peak) = maxHeights(peak)

            // Build towards the peak from the left
            for (i <- (peak - 1) to 0 by -1) {
                leftHeights(i) = Math.min(leftHeights(i + 1) - 1, maxHeights(i))
                if (leftHeights(i) == 0) leftHeights(i) = 1 // Ensure minimum height of 1
            }

            // Build away from the peak towards the right
            for (i <- (peak + 1) until n) {
                rightHeights(i) = Math.min(rightHeights(i - 1) - 1, maxHeights(i))
                if (rightHeights(i) == 0) rightHeights(i) = 1 // Ensure minimum height of 1
            }

            // Calculate sum and update maxSum
            val currentSum = (leftHeights ++ rightHeights).sum - maxHeights(peak) // Subtract peak counted twice
            maxSum = Math.max(maxSum, currentSum)
        }

        maxSum
    }
}
