object Solution {
    def maximumSumOfHeights(maxHeights: List[Int]): Long = {
        val n = maxHeights.length
        val leftToRight = Array.fill(n)(0)
        val rightToLeft = Array.fill(n)(0)

        // From left to right: find max achievable heights
        leftToRight(0) = maxHeights(0)
        for (i <- 1 until n) {
            leftToRight(i) = Math.min(leftToRight(i - 1) + 1, maxHeights(i))
        }

        // From right to left: find max achievable heights
        rightToLeft(n - 1) = maxHeights(n - 1)
        for (i <- (n - 2) to 0 by -1) {
            rightToLeft(i) = Math.min(rightToLeft(i + 1) + 1, maxHeights(i))
        }

        // Calculate the maximum sum of heights for the mountain
        (0 until n).map(i => Math.min(leftToRight(i), rightToLeft(i)).toLong).sum
    }
}
