class Solution {
    func maximumSumOfHeights(_ maxHeights: [Int]) -> Int {
        let n = maxHeights.count
        var forward = Array(repeating: 0, count: n)
        var backward = Array(repeating: 0, count: n)

        // Ascending part of the mountain
        forward[0] = maxHeights[0]
        for i in 1..<n {
            forward[i] = min(forward[i-1] + 1, maxHeights[i])
        }

        // Descending part of the mountain
        backward[n-1] = maxHeights[n-1]
        for i in stride(from: n-2, through: 0, by: -1) {
            backward[i] = min(backward[i+1] + 1, maxHeights[i])
        }

        // Find the minimum of forward and backward at each position
        // to ensure the mountain property and calculate the sum
        var maxSum = 0
        for i in 0..<n {
            maxSum += min(forward[i], backward[i])
        }

        return maxSum
    }
}
