class Solution {
    func maximumSumOfHeights(_ maxHeights: [Int]) -> Int {
        let n = maxHeights.count
        var dp = Array(repeating: 0, count: n + 1)

        for i in 1...n {
            dp[i] = maxHeights[i - 1]
        }

        // Calculate the prefix sum to simplify the sum calculation later
        for i in 1...n {
            dp[i] += dp[i - 1]
        }

        var result = 0

        for peak in 0..<n {
            var left = 1, right = min(peak + 1, n - peak)

            while left < right {
                let mid = (left + right + 1) / 2
                if maxHeights[peak - mid + 1...peak].min()! >= mid && maxHeights[peak...peak + mid - 1].min()! >= mid {
                    left = mid
                } else {
                    right = mid - 1
                }
            }

            let total = dp[peak + left] - dp[peak - left + 1] + left * (maxHeights[peak] - left + 1)
            result = max(result, total)
        }

        return result
    }
}
