function maximumSumOfHeights(maxHeights: number[]): number {
    const n = maxHeights.length;
    let maxSum = 0;

    for (let peak = 0; peak < n; peak++) {
        const heights = Array(n).fill(0);
        heights[peak] = maxHeights[peak];

        // Calculate maximum heights to the left of the peak
        for (let i = peak - 1; i >= 0; i--) {
            heights[i] = Math.min(heights[i + 1], maxHeights[i]);
        }

        // Calculate maximum heights to the right of the peak
        for (let i = peak + 1; i < n; i++) {
            heights[i] = Math.min(heights[i - 1], maxHeights[i]);
        }

        // Sum the heights for the current configuration
        const sum = heights.reduce((acc, height) => acc + height, 0);

        // Update the maximum sum
        maxSum = Math.max(maxSum, sum);
    }

    return maxSum;
}
