public class Solution {
    public long MaximumSumOfHeights(IList<int> maxHeights) {
        long maxSum = 0;

        for (int peak = 0; peak < maxHeights.Count; peak++) {
            long sum = maxHeights[peak];
            int minHeight = maxHeights[peak];

            // Expand left from the peak
            for (int i = peak - 1; i >= 0; i--) {
                minHeight = Math.Min(minHeight - 1, maxHeights[i]);
                if (minHeight <= 0) break; // Ensure height is at least 1
                sum += minHeight;
            }

            minHeight = maxHeights[peak];
            // Expand right from the peak
            for (int i = peak + 1; i < maxHeights.Count; i++) {
                minHeight = Math.Min(minHeight - 1, maxHeights[i]);
                if (minHeight <= 0) break; // Ensure height is at least 1
                sum += minHeight;
            }

            maxSum = Math.Max(maxSum, sum);
        }

        return maxSum;
    }
}
