public class Solution {
    public long MaximumSumOfHeights(IList<int> maxHeights) {
        int n = maxHeights.Count;
        long[] leftMax = new long[n];
        long[] rightMax = new long[n];

        // Initialize leftMax with the first element
        leftMax[0] = maxHeights[0];
        // Calculate max height from the left
        for (int i = 1; i < n; i++) {
            leftMax[i] = Math.Min(leftMax[i - 1] + 1, maxHeights[i]);
        }

        // Initialize rightMax with the last element
        rightMax[n - 1] = maxHeights[n - 1];
        // Calculate max height from the right
        for (int i = n - 2; i >= 0; i--) {
            rightMax[i] = Math.Min(rightMax[i + 1] + 1, maxHeights[i]);
        }

        // Calculate the maximum sum
        long maximumSum = 0;
        for (int i = 0; i < n; i++) {
            // The height at each position is the minimum of the left and right max heights
            maximumSum += Math.Min(leftMax[i], rightMax[i]);
        }

        return maximumSum;
    }
}
