#include <vector>
using namespace std;

class Solution {
public:
    long long maximumSumOfHeights(vector<int>& maxHeights) {
        int n = maxHeights.size();
        vector<long long> leftMax(n, 0), rightMax(n, 0);

        // Calculate maximum possible heights from left to right.
        leftMax[0] = maxHeights[0];
        for (int i = 1; i < n; ++i) {
            leftMax[i] = min(leftMax[i - 1] + 1, static_cast<long long>(maxHeights[i]));
        }

        // Calculate maximum possible heights from right to left.
        rightMax[n - 1] = maxHeights[n - 1];
        for (int i = n - 2; i >= 0; --i) {
            rightMax[i] = min(rightMax[i + 1] + 1, static_cast<long long>(maxHeights[i]));
        }

        // Calculate maximum sum of heights.
        long long maxSum = 0;
        for (int i = 0; i < n; ++i) {
            maxSum = max(maxSum, min(leftMax[i], rightMax[i]));
        }

        // Calculate total sum with the optimized peak.
        long long totalSum = 0;
        for (int i = 0; i < n; ++i) {
            totalSum += min(leftMax[i], rightMax[i]);
        }

        return totalSum;
    }
};
