#include <vector>
using namespace std;

class Solution {
public:
    long long maximumSumOfHeights(vector<int>& maxHeights) {
        int n = maxHeights.size();
        vector<long long> leftMax(n), rightMax(n);

        // Forward pass to calculate the maximum height each tower can be from the left
        leftMax[0] = maxHeights[0];
        for (int i = 1; i < n; ++i) {
            leftMax[i] = min(static_cast<long long>(maxHeights[i]), leftMax[i - 1] + 1);
        }

        // Backward pass to calculate the maximum height each tower can be from the right
        rightMax[n - 1] = maxHeights[n - 1];
        for (int i = n - 2; i >= 0; --i) {
            rightMax[i] = min(static_cast<long long>(maxHeights[i]), rightMax[i + 1] + 1);
        }

        // Calculate the maximum sum of heights by taking the minimum of leftMax and rightMax for each tower
        long long maxSum = 0;
        for (int i = 0; i < n; ++i) {
            maxSum += min(leftMax[i], rightMax[i]);
        }

        return maxSum;
    }
};
