#include <vector>
using namespace std;

class Solution {
public:
    long long maximumSumOfHeights(vector<int>& maxHeights) {
        int n = maxHeights.size();
        vector<long long> prefixMax(n, 0), suffixMax(n, 0);
        long long result = 0;

        // Calculate prefix max heights
        for (int i = 0; i < n; ++i) {
            if (i == 0) prefixMax[i] = maxHeights[i];
            else prefixMax[i] = min(prefixMax[i - 1] + 1, static_cast<long long>(maxHeights[i]));
        }

        // Calculate suffix max heights
        for (int i = n - 1; i >= 0; --i) {
            if (i == n - 1) suffixMax[i] = maxHeights[i];
            else suffixMax[i] = min(suffixMax[i + 1] + 1, static_cast<long long>(maxHeights[i]));
        }

        // Calculate the maximum sum of heights by taking the minimum of prefixMax and suffixMax for each tower
        for (int i = 0; i < n; ++i) {
            result += min(prefixMax[i], suffixMax[i]);
        }

        return result;
    }
};
