long long maximumSumOfHeights(int* maxHeights, int maxHeightsSize) {
    long long maxSum = 0;
    for (int peak = 0; peak < maxHeightsSize; ++peak) {
        long long sum = maxHeights[peak];
        int leftMax = maxHeights[peak];
        // Increase towards the peak from the left
        for (int i = peak - 1; i >= 0; --i) {
            leftMax = leftMax < maxHeights[i] ? leftMax : maxHeights[i];
            sum += leftMax;
            if (leftMax > 1) --leftMax;
        }
        int rightMax = maxHeights[peak];
        // Decrease away from the peak to the right
        for (int i = peak + 1; i < maxHeightsSize; ++i) {
            rightMax = rightMax < maxHeights[i] ? rightMax : maxHeights[i];
            sum += rightMax;
            if (rightMax > 1) --rightMax;
        }
        // Update maxSum if this configuration has a higher sum
        if (sum > maxSum) {
            maxSum = sum;
        }
    }
    return maxSum;
}
