#include <limits.h>

long long maximumSumOfHeights(int* maxHeights, int maxHeightsSize) {
    long long maxSum = 0;

    // Arrays to store the maximum height of tower from left to right and right to left
    int* leftMax = (int*)malloc(maxHeightsSize * sizeof(int));
    int* rightMax = (int*)malloc(maxHeightsSize * sizeof(int));

    // Initialize leftMax and rightMax arrays
    leftMax[0] = maxHeights[0];
    for (int i = 1; i < maxHeightsSize; i++) {
        leftMax[i] = (maxHeights[i] < leftMax[i-1] + 1) ? maxHeights[i] : leftMax[i-1] + 1;
    }

    rightMax[maxHeightsSize - 1] = maxHeights[maxHeightsSize - 1];
    for (int i = maxHeightsSize - 2; i >= 0; i--) {
        rightMax[i] = (maxHeights[i] < rightMax[i+1] + 1) ? maxHeights[i] : rightMax[i+1] + 1;
    }

    // Calculate the maximum possible sum of heights
    for (int i = 0; i < maxHeightsSize; i++) {
        int minHeight = (leftMax[i] < rightMax[i]) ? leftMax[i] : rightMax[i];
        maxSum += minHeight;
    }

    // Clean up
    free(leftMax);
    free(rightMax);

    return maxSum;
}
