long long maximumSumOfHeights(int* maxHeights, int maxHeightsSize) {
    // Arrays to store the max height up to that point from left and right
    int left[maxHeightsSize];
    int right[maxHeightsSize];

    left[0] = maxHeights[0];
    for (int i = 1; i < maxHeightsSize; ++i) {
        left[i] = (left[i - 1] < maxHeights[i]) ? left[i - 1] + 1 : maxHeights[i];
    }

    right[maxHeightsSize - 1] = maxHeights[maxHeightsSize - 1];
    for (int i = maxHeightsSize - 2; i >= 0; --i) {
        right[i] = (right[i + 1] < maxHeights[i]) ? right[i + 1] + 1 : maxHeights[i];
    }

    // Calculating the maximum sum of heights
    long long maxSum = 0;
    for (int i = 0; i < maxHeightsSize; ++i) {
        int minHeight = left[i] < right[i] ? left[i] : right[i];
        maxSum += minHeight;
    }

    return maxSum;
}
