func maximumSumOfHeights(maxHeights []int) int64 {
    n := len(maxHeights)
    var maxSum int64 = 0

    for peakIndex := 0; peakIndex < n; peakIndex++ {
        // Calculate sum assuming the peak is at peakIndex
        sum := int64(maxHeights[peakIndex])
        leftMax := maxHeights[peakIndex]
        rightMax := maxHeights[peakIndex]

        // Expand to the left
        for i := peakIndex - 1; i >= 0; i-- {
            leftMax = min(maxHeights[i], leftMax-1)
            sum += int64(leftMax)
        }

        // Expand to the right
        for i := peakIndex + 1; i < n; i++ {
            rightMax = min(maxHeights[i], rightMax-1)
            sum += int64(rightMax)
        }

        // Update maxSum if this configuration has a higher sum
        if sum > maxSum {
            maxSum = sum
        }
    }

    return maxSum
}

// Utility function to find the minimum of two integers
func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
