func maximumSumOfHeights(maxHeights []int) int64 {
    n := len(maxHeights)
    leftMax := make([]int, n)
    rightMax := make([]int, n)

    // Calculate maximum possible heights from the left
    leftMax[0] = maxHeights[0]
    for i := 1; i < n; i++ {
        leftMax[i] = min(maxHeights[i], leftMax[i-1]+1)
    }

    // Calculate maximum possible heights from the right
    rightMax[n-1] = maxHeights[n-1]
    for i := n - 2; i >= 0; i-- {
        rightMax[i] = min(maxHeights[i], rightMax[i+1]+1)
    }

    // Calculate the maximum sum by taking the minimum of leftMax and rightMax for each position
    var maxSum int64 = 0
    for i := 0; i < n; i++ {
        maxSum += int64(min(leftMax[i], rightMax[i]))
    }

    return maxSum
}

// Utility function to find the minimum of two integers
func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
