import java.util.List;

class Solution {
    public long maximumSumOfHeights(List<Integer> maxHeights) {
        int n = maxHeights.size();
        long[] leftMax = new long[n];
        long[] rightMax = new long[n];
        long sum = 0;

        // Calculate the maximum height possible from the left to the peak
        leftMax[0] = maxHeights.get(0);
        for (int i = 1; i < n; i++) {
            leftMax[i] = Math.min(leftMax[i - 1], maxHeights.get(i));
        }

        // Calculate the maximum height possible from the right to the peak
        rightMax[n - 1] = maxHeights.get(n - 1);
        for (int i = n - 2; i >= 0; i--) {
            rightMax[i] = Math.min(rightMax[i + 1], maxHeights.get(i));
        }

        // Calculate the maximum sum of heights by taking the maximum at each position
        // considering both left and right maximums to maintain the mountain property
        for (int i = 0; i < n; i++) {
            sum += Math.min(leftMax[i], rightMax[i]);
        }

        return sum;
    }
}
