/**
 * @param {number[]} maxHeights
 * @return {number}
 */
var maximumSumOfHeights = function(maxHeights) {
    let n = maxHeights.length;
    let maxSum = 0;

    // Function to calculate the maximum sum for a given peak
    const calculateSum = (peak) => {
        let sum = maxHeights[peak];
        let leftHeight = maxHeights[peak];
        let rightHeight = maxHeights[peak];

        // Calculate sum towards the left
        for (let i = peak - 1; i >= 0; i--) {
            leftHeight = Math.min(maxHeights[i], leftHeight - 1);
            sum += leftHeight;
        }

        // Calculate sum towards the right
        for (let i = peak + 1; i < n; i++) {
            rightHeight = Math.min(maxHeights[i], rightHeight - 1);
            sum += rightHeight;
        }

        return sum;
    };

    // Try each position as a peak and calculate the maximum sum
    for (let i = 0; i < n; i++) {
        let currentSum = calculateSum(i);
        maxSum = Math.max(maxSum, currentSum);
    }

    return maxSum;
};
