class Solution {
    fun maximumSumOfHeights(maxHeights: List<Int>): Long {
        val n = maxHeights.size
        val left = LongArray(n) { 0 }
        val right = LongArray(n) { 0 }

        // Build up from the left
        left[0] = maxHeights[0].toLong()
        for (i in 1 until n) {
            left[i] = kotlin.math.min(left[i - 1] + 1, maxHeights[i].toLong())
        }

        // Build down from the right
        right[n - 1] = maxHeights[n - 1].toLong()
        for (i in n - 2 downTo 0) {
            right[i] = kotlin.math.min(right[i + 1] + 1, maxHeights[i].toLong())
        }

        // Find the maximum sum possible with the constraints
        var maxSum = 0L
        for (i in 0 until n) {
            val minHeight = kotlin.math.min(left[i], right[i])
            maxSum += minHeight
        }

        return maxSum
    }
}
