class Solution {
    fun maximumSumOfHeights(maxHeights: List<Int>): Long {
        val n = maxHeights.size
        val leftMax = LongArray(n)
        val rightMax = LongArray(n)

        // Forward pass: find max height from the left (increasing)
        leftMax[0] = maxHeights[0].toLong()
        for (i in 1 until n) {
            leftMax[i] = kotlin.math.min(leftMax[i - 1] + 1, maxHeights[i].toLong())
        }

        // Backward pass: find max height from the right (decreasing)
        rightMax[n - 1] = maxHeights[n - 1].toLong()
        for (i in n - 2 downTo 0) {
            rightMax[i] = kotlin.math.min(rightMax[i + 1] + 1, maxHeights[i].toLong())
        }

        // Calculate maximum sum of heights
        var maxSum: Long = 0
        for (i in 0 until n) {
            // The actual height at each position is the min of what's achievable from both sides
            val actualHeight = kotlin.math.min(leftMax[i], rightMax[i])
            maxSum += actualHeight
        }

        return maxSum
    }
}
