class Solution {

    /**
     * @param Integer[] $maxHeights
     * @return Integer
     */
    function maximumSumOfHeights($maxHeights) {
        $n = count($maxHeights);
        $maxSum = 0;

        for ($peak = 0; $peak < $n; $peak++) {
            // Calculate maximum height for the peak
            $peakHeight = $maxHeights[$peak];
            $sum = $peakHeight;

            // Expand to the left from the peak
            $leftHeight = $peakHeight;
            for ($i = $peak - 1; $i >= 0; $i--) {
                $leftHeight = min($leftHeight - 1, $maxHeights[$i]);
                if ($leftHeight < 1) break; // Ensure the height is at least 1
                $sum += $leftHeight;
            }

            // Expand to the right from the peak
            $rightHeight = $peakHeight;
            for ($i = $peak + 1; $i < $n; $i++) {
                $rightHeight = min($rightHeight - 1, $maxHeights[$i]);
                if ($rightHeight < 1) break; // Ensure the height is at least 1
                $sum += $rightHeight;
            }

            // Update maximum sum if this configuration has a higher sum
            $maxSum = max($maxSum, $sum);
        }

        return $maxSum;
    }
}
