class Solution(object):
    def maximumSumOfHeights(self, maxHeights):
        """
        :type maxHeights: List[int]
        :rtype: int
        """
        n = len(maxHeights)
        left = [0] * n
        right = [0] * n
        sum = 0

        # Left to right
        for i in range(n):
            if i == 0:
                left[i] = maxHeights[i]
            else:
                left[i] = min(left[i-1] + 1, maxHeights[i])

        # Right to left
        for i in range(n-1, -1, -1):
            if i == n-1:
                right[i] = maxHeights[i]
            else:
                right[i] = min(right[i+1] + 1, maxHeights[i])

        # Calculate the maximum sum
        for i in range(n):
            sum += min(left[i], right[i])

        return sum
