from typing import List

class Solution:
    def maximumSumOfHeights(self, maxHeights: List[int]) -> int:
        n = len(maxHeights)
        prefix_max = [0] * n
        suffix_max = [0] * n
        highest = 0

        # Forward pass to find max height at each position from the left
        for i in range(n):
            if i == 0:
                highest = maxHeights[i]
            else:
                highest = min(highest + 1, maxHeights[i])
            prefix_max[i] = highest

        highest = 0
        # Backward pass to find max height at each position from the right
        for i in range(n - 1, -1, -1):
            if i == n - 1:
                highest = maxHeights[i]
            else:
                highest = min(highest + 1, maxHeights[i])
            suffix_max[i] = highest

        # Calculate the maximum possible sum by taking the minimum of
        # prefix_max and suffix_max at each position
        result = sum(min(prefix_max[i], suffix_max[i]) for i in range(n))
        return result
