from typing import List

class Solution:
    def maximumSumOfHeights(self, maxHeights: List[int]) -> int:
        def calculate_max_sum(arr):
            n = len(arr)
            stack = []
            left = [0] * n
            for i in range(n):
                while stack and arr[stack[-1]] >= arr[i]:
                    stack.pop()
                left[i] = i if not stack else (i - stack[-1] - 1)
                stack.append(i)

            stack = []
            right = [0] * n
            for i in range(n - 1, -1, -1):
                while stack and arr[stack[-1]] >= arr[i]:
                    stack.pop()
                right[i] = (n - i - 1) if not stack else (stack[-1] - i - 1)
                stack.append(i)

            max_sum = 0
            for i in range(n):
                max_sum += arr[i] * (left[i] + right[i] + 1)
            return max_sum

        left_max = maxHeights.copy()
        for i in range(1, len(maxHeights)):
            left_max[i] = min(left_max[i], left_max[i - 1] + 1)

        right_max = maxHeights.copy()
        for i in range(len(maxHeights) - 2, -1, -1):
            right_max[i] = min(right_max[i], right_max[i + 1] + 1)

        final_heights = [min(left_max[i], right_max[i]) for i in range(len(maxHeights))]
        return calculate_max_sum(final_heights)
