impl Solution {
    pub fn maximum_sum_of_heights(max_heights: Vec<i32>) -> i64 {
        let n = max_heights.len();
        let mut prefix_max = vec![0; n]; // Max height from left to right
        let mut suffix_max = vec![0; n]; // Max height from right to left
        let mut result = 0;

        // Calculate prefix max
        prefix_max[0] = max_heights[0];
        for i in 1..n {
            prefix_max[i] = prefix_max[i - 1].min(max_heights[i]);
        }

        // Calculate suffix max
        suffix_max[n - 1] = max_heights[n - 1];
        for i in (0..n-1).rev() {
            suffix_max[i] = suffix_max[i + 1].min(max_heights[i]);
        }

        // Calculate the maximum sum considering each position as the peak
        for i in 0..n {
            let current_height = prefix_max[i].min(suffix_max[i]);
            result = result.max(prefix_max[i] as i64 + suffix_max[i] as i64 - current_height as i64);
        }

        // Adjust the result since the peak was counted twice
        result
    }
}
