class Solution {
    func maximumSumOfHeights(_ maxHeights: [Int]) -> Int {
        let n = maxHeights.count
        var prefixMax = Array(repeating: 0, count: n)
        var suffixMax = Array(repeating: 0, count: n)

        // Calculate prefix max (left to right)
        prefixMax[0] = maxHeights[0]
        for i in 1..<n {
            prefixMax[i] = min(prefixMax[i-1] + 1, maxHeights[i])
        }

        // Calculate suffix max (right to left)
        suffixMax[n-1] = maxHeights[n-1]
        for i in stride(from: n-2, through: 0, by: -1) {
            suffixMax[i] = min(suffixMax[i+1] + 1, maxHeights[i])
        }

        // Calculate maximum possible sum of heights
        var maxSum = 0
        for i in 0..<n {
            // Height at each position is the minimum of prefixMax and suffixMax to satisfy mountain property
            maxSum += min(prefixMax[i], suffixMax[i])
        }

        return maxSum
    }
}
