class Solution {
    func maximumSumOfHeights(_ maxHeights: [Int]) -> Int {
        let n = maxHeights.count
        var leftMax = Array(repeating: 0, count: n)
        var rightMax = Array(repeating: 0, count: n)
        var maxSum = 0

        // Forward pass to find max height we can achieve from left to i
        leftMax[0] = maxHeights[0]
        for i in 1..<n {
            leftMax[i] = min(leftMax[i-1], maxHeights[i] - 1)
        }

        // Backward pass to find max height we can achieve from right to i
        rightMax[n-1] = maxHeights[n-1]
        for i in stride(from: n-2, through: 0, by: -1) {
            rightMax[i] = min(rightMax[i+1], maxHeights[i] - 1)
        }

        // Calculate the maximum sum of heights by taking the higher possible value
        // between left and right adjustments for each tower
        for i in 0..<n {
            maxSum += min(maxHeights[i], max(leftMax[i], rightMax[i]) + 1)
        }

        return maxSum
    }
}
