class Solution {
    func maximumSumOfHeights(_ maxHeights: [Int]) -> Int {
        // Precompute the maximum height we can achieve on the left and right for each position
        let n = maxHeights.count
        var leftMax = Array(repeating: 0, count: n)
        var rightMax = Array(repeating: 0, count: n)

        leftMax[0] = maxHeights[0]
        for i in 1..<n {
            leftMax[i] = min(leftMax[i - 1] + 1, maxHeights[i])
        }

        rightMax[n - 1] = maxHeights[n - 1]
        for i in stride(from: n - 2, through: 0, by: -1) {
            rightMax[i] = min(rightMax[i + 1] + 1, maxHeights[i])
        }

        // Calculate the maximum sum by choosing the minimum between leftMax and rightMax for each position
        var sum = 0
        for i in 0..<n {
            sum += min(leftMax[i], rightMax[i])
        }

        return sum
    }
}
