function maximumSumOfHeights(maxHeights: number[]): number {
    const n = maxHeights.length;
    let leftSum = new Array(n).fill(0);
    let rightSum = new Array(n).fill(0);
    let maxSum = 0;

    // Calculate leftSum for the increasing part
    leftSum[0] = maxHeights[0];
    for (let i = 1; i < n; i++) {
        leftSum[i] = Math.min(maxHeights[i], leftSum[i - 1] + 1);
    }

    // Calculate rightSum for the decreasing part
    rightSum[n - 1] = maxHeights[n - 1];
    for (let i = n - 2; i >= 0; i--) {
        rightSum[i] = Math.min(maxHeights[i], rightSum[i + 1] + 1);
    }

    // Find the maximum possible sum of heights
    for (let i = 0; i < n; i++) {
        // The minimum of leftSum and rightSum ensures we do not exceed maxHeights[i]
        maxSum = Math.max(maxSum, Math.min(leftSum[i], rightSum[i]));
    }

    // Calculate total maximum sum by summing the optimal heights at each position
    let totalMaxSum = 0;
    for (let i = 0; i < n; i++) {
        totalMaxSum += Math.min(leftSum[i], rightSum[i]);
    }

    return totalMaxSum;
}
