function maximumSumOfHeights(maxHeights: number[]): number {
    const n = maxHeights.length;
    let maxSum = 0;

    // Calculate prefix max to ensure non-decreasing order from left to peak
    let left = new Array(n);
    left[0] = maxHeights[0];
    for (let i = 1; i < n; i++) {
        left[i] = Math.min(maxHeights[i], left[i - 1]);
    }

    // Calculate suffix max to ensure non-increasing order from peak to right
    let right = new Array(n);
    right[n - 1] = maxHeights[n - 1];
    for (let i = n - 2; i >= 0; i--) {
        right[i] = Math.min(maxHeights[i], right[i + 1]);
    }

    // Iterate through all possible peaks
    for (let peak = 0; peak < n; peak++) {
        let currentSum = 0;
        let minHeight = maxHeights[peak];

        // Sum heights to the left of the peak
        for (let i = peak - 1; i >= 0; i--) {
            minHeight = Math.min(minHeight, left[i]);
            currentSum += minHeight;
        }

        minHeight = maxHeights[peak];
        currentSum += minHeight; // Add peak height

        // Sum heights to the right of the peak
        for (let i = peak + 1; i < n; i++) {
            minHeight = Math.min(minHeight, right[i]);
            currentSum += minHeight;
        }

        maxSum = Math.max(maxSum, currentSum);
    }

    return maxSum;
}
