public class Solution {
    public bool CanSplitArray(IList<int> nums, int m) {
        // Directly calculate the total sum and check if it's >= m.
        int totalSum = nums.Sum();
        // If total sum is < m, it's impossible to meet the condition without splitting.
        if (totalSum < m && nums.Count > 1) return false;

        // Check if any possible split can meet the criteria.
        // Since we need to check for any possible way of splitting,
        // the simplest case is when no split is needed or when just one split is enough.
        // Given the corrected understanding, we need to ensure that we're not
        // necessarily splitting into the maximum possible number of subarrays,
        // but rather that the splits meet the given conditions.

        // If the total sum is less than m but the array has only one element,
        // or the total sum is greater than or equal to m, it's already valid.
        if (nums.Count == 1 || totalSum >= m) return true;

        // For more than one element, check if any split can result in valid subarrays.
        for (int i = 0; i < nums.Count; i++) {
            int sumLeft = nums.Take(i + 1).Sum();
            int sumRight = nums.Skip(i + 1).Sum();
            // Check if both subarrays after a potential split satisfy the condition
            // or if a single element's existence is enough.
            if ((sumLeft >= m || i == 0) && (sumRight >= m || i == nums.Count - 2)) return true;
        }

        // If no split results in valid subarrays, return false.
        return false;
    }
}
