#include <vector>
#include <numeric>

using namespace std;

class Solution {
public:
    bool canSplitArray(vector<int>& nums, int m) {
        int n = nums.size();
        vector<vector<bool>> dp(n + 1, vector<bool>(n + 1, false));
        vector<int> prefixSum(n + 1, 0);

        // Calculate prefix sums for easy sum calculation of subarrays
        for (int i = 1; i <= n; ++i) {
            prefixSum[i] = prefixSum[i - 1] + nums[i - 1];
        }

        // Subarrays of length 1 are always valid
        for (int i = 0; i < n; ++i) {
            dp[i][1] = true;
        }

        // Check for all lengths from 2 to n
        for (int len = 2; len <= n; ++len) {
            for (int i = 0; i <= n - len; ++i) {
                int sum = prefixSum[i + len] - prefixSum[i];

                // If the whole subarray meets the condition
                if (sum >= m) {
                    dp[i][len] = true;
                    continue;
                }

                // Try splitting the subarray into two parts at every possible position
                for (int k = 1; k < len; ++k) {
                    if (dp[i][k] && dp[i + k][len - k]) {
                        dp[i][len] = true;
                        break;
                    }
                }
            }
        }

        // Check if the entire array can be split into valid subarrays
        return dp[0][n];
    }
};
