#include <vector>
using namespace std;

class Solution {
public:
    bool canSplitArray(vector<int>& nums, int m) {
        int n = nums.size();
        // Edge case: If the array itself meets the condition or if it's already of length n.
        if (accumulate(nums.begin(), nums.end(), 0) < m && n > 1) return false;
        return canSplit(nums, 0, n, m, 0, 0);
    }

private:
    bool canSplit(const vector<int>& nums, int start, int n, int m, int currentSum, int splits) {
        int len = nums.size();
        // If we've made enough splits and the remaining part is valid
        if (splits == n - 1) {
            return true;
        }

        for (int i = start; i < len; ++i) {
            currentSum += nums[i];
            // If current sum is valid, we can choose to split here or continue adding to currentSum
            if (currentSum >= m) {
                // Split here and check the rest
                if (canSplit(nums, i + 1, n, m, 0, splits + 1)) {
                    return true;
                }
            }
        }

        return false;
    }
};
