#include <vector>
#include <numeric>

using namespace std;

class Solution {
public:
    bool canSplitArray(vector<int>& nums, int m) {
        int left = *max_element(nums.begin(), nums.end()); // Minimum possible value of m
        int right = accumulate(nums.begin(), nums.end(), 0); // Maximum possible value of m

        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (isValid(nums, mid, m)) {
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }

        return isValid(nums, left, m);
    }

private:
    bool isValid(const vector<int>& nums, int threshold, int m) {
        int currentSum = 0;
        int count = 0; // Counts the number of arrays formed

        for (int num : nums) {
            currentSum += num;
            if (currentSum >= m) {
                count++;
                currentSum = 0;
            }
        }

        return count >= nums.size();
    }
};
