#include <stdbool.h>

bool canSplitArray(int* nums, int numsSize, int m) {
    // Base case: if the array itself meets the condition or is a single element
    int totalSum = 0;
    for (int i = 0; i < numsSize; ++i) {
        totalSum += nums[i];
    }
    if (totalSum >= m || numsSize == 1) {
        return true;
    }

    // Try to split the array
    for (int i = 1; i < numsSize; ++i) {
        int leftSum = 0;
        for (int j = 0; j < i; ++j) {
            leftSum += nums[j];
        }
        int rightSum = totalSum - leftSum;

        // Check if both subarrays after splitting meet the condition
        if ((leftSum >= m || i == 1) && (rightSum >= m || (numsSize - i) == 1)) {
            // Check if further splitting is needed and possible
            bool leftSplitPossible = (i == 1) ? true : canSplitArray(nums, i, m);
            bool rightSplitPossible = ((numsSize - i) == 1) ? true : canSplitArray(nums + i, numsSize - i, m);

            if (leftSplitPossible && rightSplitPossible) {
                return true;
            }
        }
    }

    // If no valid split is found
    return false;
}
