#include <stdbool.h>

bool canSplitArray(int* nums, int numsSize, int m) {
    // Initially, assume it's not possible to split the array
    bool possible = false;

    // Calculate the total sum to check if splitting is necessary
    int totalSum = 0;
    for (int i = 0; i < numsSize; ++i) {
        totalSum += nums[i];
    }

    // If the total sum is less than m, splitting is not possible
    if (totalSum < m) return false;

    // Since we need to split into single-element arrays, check if all elements are valid
    int validSplits = 0;
    for (int i = 0; i < numsSize; ++i) {
        if (nums[i] >= m || numsSize == 1) {
            validSplits++;
        }
    }

    // The condition to split successfully into n arrays
    if (validSplits == numsSize) {
        possible = true;
    } else {
        // Check for any possibility to split the array
        // This involves finding segments of the array that can be split according to the rules
        for (int i = 0; i < numsSize - 1; ++i) {
            int sumLeft = 0;
            for (int j = 0; j <= i; ++j) {
                sumLeft += nums[j];
            }
            int sumRight = totalSum - sumLeft;
            if ((sumLeft >= m || (i + 1) == 1) && (sumRight >= m || (numsSize - i - 1) == 1)) {
                possible = true;
                break;
            }
        }
    }

    return possible;
}
