class Solution {
  bool canSplitArray(List<int> nums, int m) {
    // Helper function to check if the array can be split according to the rules.
    bool canSplit(int start, int parts, int sum, List<int> nums, int m) {
      if (parts == 0) return sum >= m; // Last part must have sum >= m
      if (sum < m * parts) return false; // Not enough sum left for remaining parts

      int currentSum = 0;
      for (int i = start; i <= nums.length - parts; i++) {
        currentSum += nums[i];
        // Only proceed if currentSum is enough for a part, or we're making the last split.
        if (currentSum >= m && canSplit(i + 1, parts - 1, sum - currentSum, nums, m)) {
          return true;
        }
      }
      return false;
    }

    int totalSum = nums.reduce((a, b) => a + b);
    return canSplit(0, nums.length - 1, totalSum, nums, m);
  }
}
