class Solution {
  bool canSplitArray(List<int> nums, int m) {
    // Calculate the total sum of the array
    int totalSum = nums.fold(0, (sum, item) => sum + item);

    // Edge case: if total sum is less than m, cannot split as required
    if (totalSum < m) return false;

    // Since we need to check if it's possible without explicit splits,
    // we look for whether all elements can be part of subarrays that meet the criteria
    // This involves checking if the array as a whole meets the criteria
    // or if individual or combined elements can form subarrays meeting the criteria

    // Dynamic programming table for subarray sums
    List<bool> dp = List.filled(totalSum + 1, false);
    dp[0] = true; // base case

    // Update DP table based on current nums
    for (int num in nums) {
      for (int i = totalSum; i >= num; i--) {
        dp[i] = dp[i] || dp[i - num];
      }
    }

    // Check if we can form a subarray with sum >= m
    for (int i = m; i <= totalSum; i++) {
      if (dp[i]) {
        return true;
      }
    }

    return false;
  }
}
