import java.util.List;

class Solution {
    public boolean canSplitArray(List<Integer> nums, int m) {
        // Base case: If there's only one element, we don't need to split further.
        if (nums.size() == 1) return true;

        int sum = 0;
        // Calculate the total sum to see if splitting is necessary at all.
        for (int num : nums) sum += num;

        // If the total sum is less than m, we cannot split in a way that every subarray has a sum >= m
        if (sum < m) return false;

        // Try splitting the array from every possible position and check if both parts satisfy the condition.
        for (int i = 1; i < nums.size(); i++) {
            List<Integer> left = nums.subList(0, i);
            List<Integer> right = nums.subList(i, nums.size());

            if ((canSplitOrValid(left, m) && canSplitOrValid(right, m))) return true;
        }

        return false;
    }

    // Helper method to check if a subarray can be split or is already valid.
    private boolean canSplitOrValid(List<Integer> subArray, int m) {
        int sum = 0;
        for (int num : subArray) sum += num;

        // If the sum is >= m, or it's a single element array, it's valid.
        if (sum >= m || subArray.size() == 1) return true;

        // Otherwise, try to split it further.
        return canSplitArray(subArray, m);
    }
}
