/**
 * @param {number[]} nums
 * @param {number} m
 * @return {boolean}
 */
var canSplitArray = function(nums, m) {
    const n = nums.length;
    let memo = {};

    // Helper function to calculate the sum of a subarray
    const getSum = (start, end) => {
        let sum = 0;
        for (let i = start; i <= end; i++) {
            sum += nums[i];
        }
        return sum;
    };

    // Recursive function with memoization
    function canSplit(start, parts) {
        // If the start index reaches the end and we have made the required splits
        if (start == n && parts == 0) return true;
        if (start >= n || parts < 0) return false;
        let memoKey = `${start}-${parts}`;
        if (memoKey in memo) return memo[memoKey];

        // Attempt to split the array and recursively check if the remaining parts can be split
        for (let end = start; end < n; end++) {
            if (getSum(start, end) >= m || end - start == 0) {
                if (canSplit(end + 1, parts - 1)) {
                    memo[memoKey] = true;
                    return true;
                }
            }
        }

        memo[memoKey] = false;
        return false;
    }

    // We subtract 1 from 'n' because we want to split into 'n' parts, so we need 'n-1' splits.
    return canSplit(0, n);
};

// Example Test Cases
console.log(canSplitArray([2, 2, 1], 4)); // Expected output: true
