var canSplitArray = function(nums, m) {
    // Calculate the total sum to check if it's even possible to split as required.
    const totalSum = nums.reduce((acc, val) => acc + val, 0);

    // Early exit if the total sum is less than m times (n-1), meaning we cannot form n segments with each at least m.
    if (totalSum < m * (nums.length - 1)) return false;

    // Function to check if it's possible to form segments with sum >= m
    function canFormSegments(index, segmentsFormed, currentSum) {
        // Base case: if we've reached the end of the array
        if (index === nums.length) {
            // Check if we've formed exactly n-1 segments with sum >= m, and the last segment also has sum >= m
            return segmentsFormed === nums.length - 1 && currentSum >= m;
        }

        // If adding the current element exceeds or meets the required sum, we can try to form a new segment
        if (currentSum + nums[index] >= m) {
            // Case 1: Form a new segment and include the current element in the new segment
            if (canFormSegments(index + 1, segmentsFormed + 1, nums[index])) {
                return true;
            }
        }

        // Case 2: Continue adding to the current segment
        if (segmentsFormed < nums.length - 1) { // Ensure we don't form more segments than allowed
            if (canFormSegments(index + 1, segmentsFormed, currentSum + nums[index])) {
                return true;
            }
        }

        return false;
    }

    // Start recursion from the first element, no segments formed yet, and current sum as 0.
    return canFormSegments(0, 0, 0);
