class Solution {
    fun canSplitArray(nums: List<Int>, m: Int): Boolean {
        // Memoization table to store subproblem results
        val memo = mutableMapOf<String, Boolean>()

        // Helper function to check if we can split the array starting from index `start`
        fun canSplitFrom(start: Int, remainingSplits: Int): Boolean {
            val key = "$start,$remainingSplits" // Unique key for memoization

            // Check if result is already computed
            memo[key]?.let { return it }

            // Base case: if no more splits are needed, return true
            if (remainingSplits == 0) return true

            var sum = 0

            // Try to split the array at every possible index
            for (i in start until nums.size - remainingSplits) {
                sum += nums[i]
                // If sum >= m, check if the remaining part can be split accordingly
                if (sum >= m && canSplitFrom(i + 1, remainingSplits - 1)) {
                    memo[key] = true
                    return true
                }
            }

            memo[key] = false
            return false
        }

        // Initial call: we need to split the array into (nums.size) parts
        return canSplitFrom(0, nums.size - 1)
    }
}
