class Solution {
    fun canSplitArray(nums: List<Int>, m: Int): Boolean {
        // Recursive function to check if the array can be split into valid subarrays
        fun canSplit(start: Int, end: Int, subarrays: Int): Boolean {
            // Base case: if only one subarray is needed, check if the current segment meets the criteria
            if (subarrays == 1) return nums.subList(start, end + 1).sum() >= m || end - start == 0

            for (i in start until end) {
                val leftSum = nums.subList(start, i + 1).sum()
                // If the left sum is valid, recursively check the right part
                if (leftSum >= m && canSplit(i + 1, end, subarrays - 1)) {
                    return true
                }
            }
            return false
        }

        // Start with the assumption that we need to split the array into n subarrays, where n is the length of the array
        // Since we start counting subarrays from 1 but our function uses 0-based indexing, adjust accordingly
        return canSplit(0, nums.size - 1, nums.size)
    }
}
