class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $m
     * @return Boolean
     */
    function canSplitArray($nums, $m) {
        // Calculate total sum to check if initial array can be split at all
        $totalSum = array_sum($nums);
        if ($totalSum < $m) return false; // Cannot split into required parts

        // Initialize memoization array
        $memo = [];

        // Helper function to check if we can make the splits recursively
        $canSplit = function($index, $parts, $currentSum) use ($nums, $m, &$memo) {
            // Base cases
            if ($index == count($nums)) {
                return $parts == 1 && $currentSum >= $m;
            }
            if ($parts == 1) {
                return array_sum(array_slice($nums, $index)) >= $m;
            }

            $key = $index . '-' . $parts . '-' . $currentSum;
            if (isset($memo[$key])) return $memo[$key];

            // Try splitting here
            if ($currentSum >= $m) {
                if ($canSplit($index + 1, $parts - 1, $nums[$index])) {
                    $memo[$key] = true;
                    return true;
                }
            }

            // Continue without splitting
            if ($canSplit($index + 1, $parts, $currentSum + $nums[$index])) {
                $memo[$key] = true;
                return true;
            }

            // Memoize and return result
            $memo[$key] = false;
            return false;
        };

        // Try splitting the array into n arrays from index 0
        for ($i = 1; $i <= count($nums); $i++) {
            if ($canSplit(0, $i, 0)) return true;
        }

        return false;
    }
}
