class Solution {

    /**
     * Check if it's possible to split the array following the given rules.
     * 
     * @param Integer[] $nums Array of integers to split.
     * @param Integer $m The minimum sum for a subarray to be valid if its length > 1.
     * @return Boolean True if it's possible to split the array into n arrays under the conditions; otherwise, false.
     */
    function canSplitArray($nums, $m) {
        $n = count($nums);

        // Early check: if single element is less than m and m > 0, split is impossible.
        foreach ($nums as $num) {
            if ($num < $m && $m > 0) return false;
        }

        // Dynamic programming approach to check if we can form subarrays with sum >= m
        // dp[i] represents if we can split array from 0 to i into valid subarrays.
        $dp = array_fill(0, $n, false);
        $cumulativeSum = array_fill(0, $n, 0);
        $cumulativeSum[0] = $nums[0];

        // Precompute cumulative sums to efficiently calculate segment sums later
        for ($i = 1; $i < $n; $i++) {
            $cumulativeSum[$i] = $cumulativeSum[$i - 1] + $nums[$i];
        }

        // Initialize dp[0] based on the first element
        $dp[0] = $nums[0] >= $m;

        // Fill dp array
        for ($i = 1; $i < $n; $i++) {
            for ($j = 0; $j <= $i; $j++) {
                $sum = $cumulativeSum[$i] - ($j > 0 ? $cumulativeSum[$j - 1] : 0);
                if ($sum >= $m) {
                    if ($j == 0 || $dp[$j - 1]) {
                        $dp[$i] = true;
                        break;
                    }
                }
            }
        }

        // If the entire array can be validly split, the last element in dp will be true.
        return $dp[$n - 1];
    }
}
