class Solution {

    /**
     * Helper function to check if it's possible to split the array into given number of parts
     * with each part having a sum of at least `mid`.
     */
    private function isValid($nums, $m, $mid) {
        $count = 1;
        $sum = 0;
        foreach ($nums as $num) {
            $sum += $num;
            if ($sum >= $mid) {
                $count++;
                $sum = 0;
            }
        }
        return $count >= $m;
    }

    /**
     * Main function to determine if the array can be split as per the problem statement.
     */
    function canSplitArray($nums, $m) {
        $left = max($nums); // Minimum possible value for a split
        $right = array_sum($nums); // Maximum possible value for a split

        while ($left < $right) {
            $mid = $left + (int)(($right - $left) / 2);
            if ($this->isValid($nums, $m, $mid)) {
                $right = $mid;
            } else {
                $left = $mid + 1;
            }
        }

        // After the loop, left is the smallest sum we can achieve for a valid split
        // Check if we can achieve exactly m splits with this sum
        return $this->isValid($nums, $m, $left);
    }
}
