class Solution(object):
    def canSplitArray(self, nums, m):
        """
        :type nums: List[int]
        :type m: int
        :rtype: bool
        """
        def isValid(sub):
            sub_sum, count = 0, 1
            for num in nums:
                sub_sum += num
                if sub_sum > sub:
                    sub_sum = num
                    count += 1
            return count <= m

        left, right = max(nums), sum(nums)
        while left < right:
            mid = (left + right) // 2
            if isValid(mid):
                right = mid
            else:
                left = mid + 1
        return left >= m

        total = sum(nums)
        if total < m:
            return False

        dp = [[False] * (m + 1) for _ in range(len(nums) + 1)]
        dp[0][0] = True

        for i in range(1, len(nums) + 1):
            for j in range(1, m + 1):
                if j >= nums[i - 1]:
                    dp[i][j] = dp[i - 1][j] or dp[i - 1][j - nums[i - 1]]

        return dp[len(nums)][m]
